Game.create(store_id: 2, name: "The Battle of Lizard", description: "In The Battle of Lizard, a.k.a. 蜥蓋, players each control a group of lizards on an island competing for survival — long-term survival, that is, because when breeding time arrives, these male lizards (Uta stansburiana) will fight one another for land and the right to reproduction. To succeed, you need to block others to keep them from reproducing!", min_players: 2, max_players: 6, min_playtime: 20, max_playtime: 40, link_url: "https://boardgamegeek.com/boardgame/269731/battle-lizard", photo_url: "https://cf.geekdo-images.com/original/img/5UB3a05fBlgACtNfKN7WtrCAtt8=/0x0/pic4885364.png")
Game.create(store_id: 2, name: "Lords, Ladies & Lizards: The Adventure Game", description: "Up to six Players get a chance to create and develop Characters through a complex journey that involves strategy, economics, politics and war! Players are able to play as either a Lord, a Lady or a Lizard, each with unique properties and leveling bonuses. Over the span of many game years, players will face personal struggles with jealousy and greed, deceit and rage! They will travel across three continents by land, by sea and by air, clearing the way of treacherous monsters. Players must choose which path to take... the way of the warrior? Or the lavish life of the landlord? They can build wealth and recognition through Theatres and entertainment. Or buy and trade treasures and build kingdoms that will rise and fall! They will be forced into war against one other all for the right to reign supreme! But there will only be one winner and that is the one who defeats the Dragon! Do YOU have what it takes to defeat the Dragon!?", min_players: 2, max_players: 6, min_playtime: 120, max_playtime: 180, link_url: "https://boardgamegeek.com/boardgame/102706/lords-ladies-lizards-adventure-game", photo_url: "https://cf.geekdo-images.com/imagepage/img/ak4Vr--YCzc_ezmiFa62Zd3U0VE=/fit-in/900x600/filters:no_upscale()/pic1166275.jpg")
Game.create(store_id: 2, name: "Terrible Lizards", description: "In Terrible Lizards you build your own dinosaur and fight against the reptile of your opponent. All actions including the dino parts are represented in the play of cards.", min_players: 2, max_players: 4, min_playtime: 20, max_playtime: 60, link_url: "https://boardgamegeek.com/boardgame/178610/terrible-lizards", photo_url: "https://cf.geekdo-images.com/imagepage/img/MWfJboXEL6FIoiPr0w3LFyuZAkc=/fit-in/900x600/filters:no_upscale()/pic2692626.jpg")
Game.create(store_id: 2, name: "Thrilling Expeditions: Valley of the Thunder Lizard ", description: "Thrilling Expeditions: Valley of the Thunder Lizard allows players to play big game hunters in a lost valley populated with creatures from before the rise of man, or have their pirates going ashore on an island where Neanderthals battle Saurians, or time traveling tourists suddenly stranded in Earth’s distant past.", min_players: 1, max_players: 6, min_playtime: 60, max_playtime: 90, link_url: "https://boardgamegeek.com/boardgameexpansion/36327/thrilling-expeditions-valley-thunder-lizard", photo_url: "https://cf.geekdo-images.com/imagepage/img/MVr_jc8ZKyCZ-bGa86CeWRtJp7g=/fit-in/900x600/filters:no_upscale()/pic329991.jpg")
Game.create(store_id: 2, name: "Lickin' Lizards ", description: "A rotating base contains bug or spider disks. Each player has a plastic lizard with an 'Elastomeric' tongue (patents no. 4,369,284 and 4,618,213) which can stretch to 750% of its normal length. Try to collect disks using the sticky, stretchy tongue. Three different variations are included.", min_players: 2, max_players: 4, min_playtime: 20, max_playtime: 40, link_url: "https://boardgamegeek.com/boardgame/6570/lickin-lizards", photo_url: "https://cf.geekdo-images.com/imagepage/img/a56dLg7vz-mgqCoFdMgKraaqHTw=/fit-in/900x600/filters:no_upscale()/pic1975881.jpg")
